				<vsize>20</vsize>
				<screen_items>\n"; 
	$export_row=$export_row."<screen><name>".$screen_name."</name><hsize>2</hsize><vsize>20</vsize><screen_items>"; 			

	my $x=0;
	my $y=0;
	my $status_y=0;
	
	foreach my $server (@servers)
	{
		chomp $server;
		my $host=cut($server,"0","_");
		if ($host ne "host")
		{
			my $server_name=cut($server,"0",".");
			my $region=uc(cut($server_name,"1","-"));
			print "[main] server = ".$server." server_name = ".$server_name." region = ".$region."\n";
			my $res = check_service($server_name,$service);
			if ($res == 1)
			{			
				print export "                <screen_item>
						<resourcetype>0</resourcetype>
						<width>600</width>
						<height>200</height>
						<x>".$x."</x>
						<y>".$y."</y>
						<colspan>1</colspan>
						<rowspan>1</rowspan>
						<elements>0</elements>
						<valign>0</valign>
						<halign>0</halign>
						<style>0</style>
						<url/>
						<dynamic>1</dynamic>
						<sort_triggers>0</sort_triggers>
						<resource>
							<name>".$graph_name."</name>
							<host>".$server_name."</host>
						</resource>
					</screen_item>\n";
				$export_row=$export_row."<screen_item><resourcetype>0</resourcetype><width>600</width><height>200</height><x>".$x."</x><y>".$y."</y><colspan>1</colspan><rowspan>1</rowspan><elements>0</elements><valign>0</valign><halign>0</halign><style>0</style><url/><dynamic>1</dynamic><sort_triggers>0</sort_triggers><resource><name>".$graph_name."</name><host>".$server_name."</host></resource></screen_item>";
				if ($status_y == 0)
				{
					$x=1;
					$status_y=1;
				}
				elsif ($status_y == 1)
				{
					$x=0;
					$y++;
					$status_y=0;
				}				
			}
		}
	}
	print export "</screen_items>\n";	
	$export_row=$export_row."</screen_items>";	
	print export "</screen>\n";	
	$export_row=$export_row."</screen>";
	return ($export_row);
	close(export);
}

sub get_date
{
	my $type=shift;
	my $tm_now = localtime;
	my $date;
	#my $datetime=($tm_now->year+1900).'-'.(($tm_now->mon)+1).'-'.$tm_now->mday.'_'.$tm_now->hour.':'.$tm_now->min.':'.$tm_now->sec;
	$mon=$tm_now->mon+1;
	$mday=$tm_now->mday;
	$hour=$tm_now->hour;
	$min=$tm_now->min;
	$sec=$tm_now->sec;
	my $mon_str;
	my $mday_str;
	my $hour_str;
	my $min_str;
	my $sec_str;
	
	#month
	if ($mon < 10)
	{
		$mon_str="0".$mon;
	}
	else
	{
		$mon_str=$mon;
	}	
	#day
	if ($mday < 10)
	{
		$mday_str="0".$mday;
	}
	else
	{
		$mday_str=$mday;
	}		
	#hour	
	if ($hour < 10)
	{
		$hour_str="0".$hour;
	}
	else
	{
		$hour_str=$hour;
	}
	#min		
	if ($min < 10)
	{
		$min_str="0".$min;
	}
	else
	{
		$min_str=$min;
	}
	#sec
	if ($sec < 10)
	{
		$sec_str="0".$sec;
	}
	else
	{
		$sec_str=$sec;
	}
	if ($type eq "folder")
	{
		$date=($tm_now->year+1900).($mon_str).$mday_str.$hour_str.$min_str;
	}
	else
	{
		$date=($tm_now->year+1900).'-'.($mon_str).'-'.$mday_str.'T'.$hour_str.':'.$min_str.':'.$sec_str."Z";
	}
	return $date;
}

sub check_service
{
	my $server=shift;
	my $service=shift;
	open(all_servers,"/export/geo/servers/geo_servers.dat");	
	my @all_servers=<all_servers>;
	$str = $service.";".$server;
	if (grep(/$str/,@all_servers))
	{
		return 1;
	}
	else
	{
		return 0;
	}
	close(all_servers);
}

sub cut
{
	(my $string,my $number,my $delimeter) = @_;
	my @a;
	if ($delimeter eq ".")
	{
		@a=split("\\.", $string);
	}
	else
	{
		@a=split("$delimeter", $string);
	}
	my $value=$a[$number];
	return $value;
}